import React, { Component } from 'react';
import './App.css';
import { Statistics } from './Statistics/statistics';
import { Notification } from './Notification/notification';
import { Options } from './Options/options';

export class Feedback extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  goodIncrement = () => {
    console.log(e.currentTarget);
    this.setState(stateGood => ({
      good: stateGood.good + 1,
    }));
  };
  neutralIncrement = () => {
    this.setState(statNeutral => ({
      neutral: statNeutral.neutral + 1,
    }));
  };
  badIncrement = () => {
    this.setState(stateBad => ({
      bad: stateBad.bad + 1,
    }));
  };

  render() {
    const state = this.state;
    const total = this.state.good + this.state.neutral + this.state.bad;
    const positivePercentage = Math.round((state.good / total) * 100);
    return (
      <div className="container">
        <div>
          <p>Please leave feedback</p>
          <Options />
        </div>
        {total === 0 ? (
          <Notification message={'Feedback no given'} />
        ) : (
          <Statistics
            good={state.good}
            neutral={state.neutral}
            bad={state.bad}
            total={total}
            positivePercentage={positivePercentage}
          />
        )}
      </div>
    );
  }
}
