import React, { Component } from 'react';
import './App.css';
import { Statistics } from './Statistics/statistics';
import { Notification } from './Notification/notification';
import { Options } from './Options/options';

export class Feedback extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  handleVoteCounter = e => {
    const selectedFeedback = e.currentTarget.value;
    console.log(
      'кликнули в кнопку ',
      selectedFeedback,
      typeof selectedFeedback
    );

    // selectedFeedback === 'good' &&
    this.setState(prev => ({
      [selectedFeedback]: prev[selectedFeedback] + 1, // УРА! ПЕРЕМОГА ;))
    }));
  };
  // goodIncrement = () => {
  //   this.setState(stateGood => ({
  //     good: stateGood.good + 1,
  //   }));
  // };
  // neutralIncrement = () => {
  //   this.setState(statNeutral => ({
  //     neutral: statNeutral.neutral + 1,
  //   }));
  // };
  // badIncrement = () => {
  //   this.setState(stateBad => ({
  //     bad: stateBad.bad + 1,
  //   }));
  // };

  render() {
    const { good, neutral, bad } = this.state;
    const total = good + neutral + bad;
    const positivePercentage = Math.round((good / total) * 100);
    return (
      <div className="container">
        <div>
          <p>Please leave feedback</p>
          <Options
            onLeaveFeedback={this.handleVoteCounter}
            options={Object.values(this.state)}
          />
        </div>
        {total === 0 ? (
          <Notification message={'Feedback no given'} />
        ) : (
          <Statistics
            good={good}
            neutral={neutral}
            bad={bad}
            total={total}
            positivePercentage={positivePercentage}
          />
        )}
      </div>
    );
  }
}
